                                <g id="Raya-derecha-del-cabello" serif:id="Raya derecha del cabello" transform="matrix(1.026912,0,0,1.370331,2716.535433,0)">
                                    <path d="M1614,997C1614,997 1636.556,912.016 1644,908" style="fill:none;stroke:black;stroke-width:8.33px;"/>
